    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        boolean error = false;
try {
double radio = Double.parseDouble(txtRadio.getText());
esfera esfera1 = new esfera(radio);

txtVolumen.setText("Volumen (cm3): " + String.valueOf(esfera1.calcular_volumen())); 
txtSuperficie.setText("Superficie (cm2): " + String.valueOf(esfera1.calcular_superficie()));
} catch (Exception e) {
error = true;
} finally {
if(error) {
    JOptionPane.showMessageDialog(null,"Campo nulo o error en formato de n√∫mero","Error", JOptionPane.ERROR_MESSAGE);
}
}
    
    }//GEN-LAST:event_btnCalcularActionPerformed

