    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
    piramide piramide1;
    boolean error = false;
    double base = 0;
    double altura = 0;
    double apotema = 0;
try {
base = Double.parseDouble(txtBase.getText());
altura = Double.parseDouble(txtAltura.getText());
apotema = Double.parseDouble(txtApotema.getText());
piramide1 = new piramide(base, altura, apotema);

txtVolumen.setText("Volumen (cm3): " + String.valueOf(piramide1.calcular_volumen()));
txtSuperficie.setText("Superficie (cm2): " + String.valueOf(piramide1.calcular_superficie()));
} catch (Exception e) {
error = true;
} finally {
if (error) {
JOptionPane.showMessageDialog(null, "Campo nulo o error en formato de n√∫mero","Error",JOptionPane.ERROR_MESSAGE);
}
}

    }//GEN-LAST:event_btnCalcularActionPerformed

